import { createContext } from "react";

const themes = {
  light: {
    foreground: "#000",
    background: "#eee",
  },
  dark: {
    foreground: "#fff",
    background: "#222",
  },
};
//Context 객체
const ThemeContext = createContext(themes.light);

function ThemdButton() {
  return <button onClick={ThemeContext}>I am styled by theme context</button>;
}

function Toolbar(props) {
  return (
    <div>
      <ThemdButton />
    </div>
  );
}

function App() {
  return (
    <ThemeContext.Provider value={themes.dark}>
      <Toolbar />
    </ThemeContext.Provider>
  );
}

export default App;
