import React, { useContext } from "react";
import { OrderContext } from "../../context/OrderContext";

const SummaryPage = () => {
  const [orderData] = useContext(OrderContext);
  console.log(orderData);

  const productArray = Array.from(orderData.products);
  console.log(productArray);

  const productList = productArray.map(([key, value]) => (
    <li>
      {key} : {value}
    </li>
  ));

  const hasOptions = orderData.options.size > 0;
  let optionsDisplay = null;
  if (hasOptions) {
    const optionsArray = Array.from(orderData.options.keys());
    const optionList = optionsArray.map((key) => <li key={key}>{key} 상품</li>);
    optionsDisplay = (
      <>
        <p>옵션: {orderData.totals.options}</p>
        <ul>{optionList}</ul>
      </>
    );
  }
  return (
    <div>
      <h1>주문 확인</h1>
      <p>여행 상품 : {orderData.totals.products}</p>
      <ul>{productList}</ul>
      {optionsDisplay}

      <form>
        <input id="confirm-checkbox" type="checkbox" />
        <label htmlFor="confirm-checkbox">주문하려는 것을 확인하셨나요?</label>
        <br />
        <button type="submit">주문 확인</button>
      </form>
    </div>
  );
};

export default SummaryPage;
