import React, { useEffect, useState } from "react";
import axios from "axios";
const Type = ({ orderType }) => {
  const [itmes, setItmes] = useState([]);

  useEffect(() => {
    fetchItems(orderType);
  }, [orderType]);

  const fetchItems = async (orderType) => {
    try {
      const response = await axios.get(`http://localhos:4000/${orderType}`);
      console.log(response.data);
      setItmes(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>주문 종류</h2>
      <p>하나의 가격</p>
      <p>총 가격</p>
      <div
        style={{
          display: "flex",
          flexDirection: orderType === "options" ? "column" : "row",
        }}
      ></div>
    </div>
  );
};

export default Type;
