import React from "react";

export const Product = ({ name, imagePath, updateItemCount }) => {
  return (
    <div style={{ textAlign: "center" }}>
      <img
        style={{ width: "75%" }}
        src={`http://localhost:4000/${imagePath}`}
        alt={`${name} product`}
      />

      <form style={{ marginTop: "10px" }}>
        <label style={{ textAlign: "right" }}>{name}</label>
        <input
          type="number"
          defaultValue={0}
          min="0"
          name="quantity"
          id="quantity"
        />
      </form>
    </div>
  );
};
